var ConsoleTemplate = require('./ConsoleTemplate');
var proto = require('proto');

var Log = proto.extend(ConsoleTemplate, {
	level: null,
	suffix: '\n',
	mergePropertyNames: ConsoleTemplate.prototype.mergePropertyNames.concat('level'),

	constructor: function(string, level){
		//if( typeof level != 'string' ) throw new TypeError('Log must define a level');
		this.level = level;
		ConsoleTemplate.call(this, string);
	},

	toString: function(){
		var output;

		output = this.level && this.level != 'log' ? this.level + ':' : '';
		output+= ConsoleTemplate.prototype.toString.call(this);

		return output;
	},

	toStylizedString: function(){
		var output;

		output = this.level && this.level != 'log' ? this.stylizeValue(this.level, this.level) + ': ' : '';
		output+= ConsoleTemplate.prototype.toStylizedString.call(this);

		return output;
	},

	toJSON: function(){
		var obj = ConsoleTemplate.prototype.toJSON.call(this);
		obj.level = this.level;
		return obj;
	}
});

Log.prototype.registerStyles({
	//'log': {},
	'info': ['green'],
	'help': ['cyan'],
	'warn': ['yellow', 'bold'],
	'debug': ['grey', 'bold'],
	'error': ['red', 'bold']
});

module.exports = Log;

