var PropertyUpdater = require('PropertyUpdater');
var implementPropertyUpdater = require('Object.Implement');

var updater = new PropertyUpdater(function(object, name, owner){
	name = typeof name == 'symbol' ? name.toString() : String(name);
	if( !(name in object) ){
		implementPropertyUpdater.set(object, name, owner);
	}
});

module.exports = updater;
Object.complement = updater.update;