var PropertyUpdater = require('PropertyUpdater');
var updater, set;

if( Object.defineProperty ){
	set = function(object, name, owner){
		if( !(name in object) ){
			Object.defineProperty(object, name, Object.getOwnPropertyDescriptor(owner, name));
		}
	};
}
else{
	set = function(object, name, owner){
		if( !(name in object) ){
			object[name] = owner[name];
		}
	};
}

updater = new PropertyUpdater(set);

module.exports = updater;
Object.complete = updater.update;