var PropertyUpdater = require('PropertyUpdater');
var set;

if( Object.defineProperty ){
	set = function(object, name, owner){
		var descriptor = Object.getOwnPropertyDescriptor(owner, name);
		descriptor.enumerable = false;
		Object.defineProperty(object, name, descriptor);
	};
}
else{
	set = function(object, name, owner){
		object[name] = owner[name];
	};
}

var updater = new PropertyUpdater(set);

module.exports = updater;
Object.implement = updater.update;