require('Object.complement');
require('Object.complementProperty');
require('Object.complete');
require('Object.completeProperty');

var rootScope = typeof window == 'undefined' ? global : window;

var polyfill = function(object, name, value){
	if( arguments.length === 2 ){
		if( object === Array.prototype || String.prototype ){
			Object.complement(object, name);
		}
		else{
			Object.complete(object, name);
		}
	}
	else{
		if( object === 'global' ){
			object = rootScope;
		}

		if( object === Array.prototype || String.prototype ){
			Object.complementProperty(object, name, value);
		}
		else{
			Object.completeProperty(object, name, value);
		}
	}
};

module.exports = polyfill;
Object.polyfill = polyfill;