/*

https://api.dartlang.org/apidocs/channels/stable/#observe/observe.PropertyPath


Same as PropertyPath except path are cached
(usefull when path matters and NOT the object on wich it's tested)

*/

var PropertyPath = require('./PropertyPath');
var proto = require('@dmail/proto');

var CachedPropertyPath = proto.extend.call(PropertyPath, {
	cache: {},

	constructor: function(path){
		if( path in this.cache ){
			return this.cache[path];
		}
		this.cache[path] = this;
		CachedPropertyPath.super.constructor.call(this, path);
	}
});

module.exports = CachedPropertyPath;