var proto = require('proto');
var PropertyUpdater = proto.create({
	constructor: function(set){
		if( set ) this.set = set;
		this.update = this.setAll.bind(this);
	},

	set: function(object, name, owner){
		object[name] = owner[name];
	},

	setAll: function(object){
		var i = 1, j = arguments.length, owner, keys, n, m;

		for(;i<j;i++){
			owner = arguments[i];
			keys = Object.keys(owner);
			n = 0;
			m = keys.length;

			for(;n<m;n++){
				this.set(object, keys[n], owner);
			}
		}

		return object;
	},
});

module.exports = PropertyUpdater;