var methods = {
	trimLeft: function(){
		return String(this).replace(/^\s+/, '');
	},

	trimRight: function(){
		return String(this).replace(/\s+$/, '');
	},

	trim: function(){
		return String(this).replace(/^\s+|\s+$/g, '');
	}
};

module.exports = methods;
require('Object.polyfill')(String.prototype, methods);