var StringTemplate = require('./StringTemplate');

StringTemplate.StylizedTemplate = require('./StylizedTemplate');
StringTemplate.TemplateRepeater = require('./TemplateRepeater');

StringTemplate.supplement('repeat', function(array, separator, fn, bind){
	return new StringTemplate.TemplateRepeater(this, separator).exec(array, fn, bind);
});

module.exports = StringTemplate;