var StringTemplate = require('./StringTemplate');
var StylizedTemplate = require('./StylizedTemplate');
var TemplateRepeater = require('./TemplateRepeater');

Object.defineProto(StringTemplate, {
	StylizedTemplate: StylizedTemplate,
	TemplateRepeater: TemplateRepeater,

	repeat: function(array, separator, fn, bind){
		var repeater = new this.TemplateRepeater(this, separator);
		repeater.exec(array, fn, bind);
		return repeater;
	}
});

String.prototype.render = StringTemplate.prototype.format;

module.exports = StringTemplate;