exports['render on same template gives != result for != object'] = function(test){
	var tpl = new test.imports('Hello {name} !');

	test.equal(tpl.render({name: 'damien'}), 'Hello damien !');
	test.equal(tpl.render({name: 'sandra'}), 'Hello sandra !');
};

exports['toArray().join equivalent to toString()'] = function(test){
	var tpl = new test.imports('test');

	test.equal(tpl.toArray().join(''), tpl.toString());
};

exports['clone() keep suffix on stylizedTemplate'] = function(test){
	var tpl = new test.imports.prototype.StylizedTemplate('ok');

	tpl.suffix = 'suff';
	var clone = tpl.clone();

	test.equal(clone.suffix, tpl.suffix);
	test.equal(clone.hasOwnProperty('suffix'), true);
};