exports['cell width'] = function(test, CellLayout){
	function create(data, style){
		style.paddingLeft = 0;
		style.paddingRight = 0;

		var cellLayout = CellLayout.create({
			value: data,
			style: style
		}, data.length);
		return cellLayout;
	}

	var layout;

	layout = create('hello', {width: 'auto', minWidth: 0, maxWidth: 100});
	test.equal(layout.width, 5);

	layout = create('hello', {width: 'auto', minWidth: 10, maxWidth: 100});
	test.equal(layout.width, 10);

	layout = create('hello', {width: 'auto', minWidth: 0, maxWidth: 3});
	test.equal(layout.width, 3);

	layout = create('hello', {width: 20, minWidth: 0, maxWidth: 17});
	test.equal(layout.width, 17);

	layout = create('0123456789', {width: '20%', minWidth: 0, maxWidth: 100});
	test.equal(layout.width, 2);
};