require('@dmail/object/is');

module.exports = function(array, value){
	var index;

	if( value != value || value === 0 ){
		index = array.length;
		while( index-- && Object.is(array[index], value) );
	}
	else{
		index = Array.prototype.indexOf.call(array, value);
	}

	return index;
};

Array.exactIndexOf = module.exports;