// https://developer.mozilla.org/fr/docs/Web/JavaScript/Reference/Objets_globaux/Array/every
function every(fn, bind){
	if( this == null ){
		throw new TypeError('this null or undefined');
	}
	if( typeof fn !== 'function' ){
		throw new TypeError();
	}

	var array = Object(this), i = 0, j = array.length >>> 0, value;

	while( i < j ){
		if( i in array ){
			value = array[i];
			if( !fn.call(bind, value, i, array) ){
				return false;
			}
		}
		i++;
	}
	return true;
}

module.exports = every;
require('@dmail/object/polyfill')(Array.prototype, 'every', every);