// https://developer.mozilla.org/fr/docs/Web/JavaScript/Reference/Objets_globaux/Array/fill
function fill(value, start, end){
	var array = Object(this), length = parseInt(array.length, 10), i, j;

	start = parseInt(start, 10) || 0;
	end = end === undefined ? length : (parseInt(end, 10) || 0);

	i = start < 0 ? Math.max(length + start, 0) : Math.min(start, length);
	j = end < 0 ? Math.max(length + end, 0) :  Math.min(end, length);
	for(;i<j;i++){
		array[i] = value;
	}

	return array;
}

module.exports = fill;
require('object/polyfill')(Array.prototype, 'fill', fill);