// https://developer.mozilla.org/fr/docs/Web/JavaScript/Reference/Objets_globaux/Array/some
// Production ECMA-262, Edition 5, 15.4.4.17
// Référence : http://es5.github.io/#x15.4.4.17

function some(fn, bind){
	if( this == null ){
		throw new TypeError('Array.prototype.some called with null or undefined');
	}
	if( typeof fn !== 'function' ){
		throw new TypeError();
	}

	var array = Object(this), i = 0, j = array.length >>> 0;

	for(;i<j;i++){
		if( i in array && fn.call(bind, array[i], i, array) ){
			return true;
		}
	}

	return false;
}

module.exports = some;
require('@dmail/object/polyfill')(Array.prototype, 'some', some);