var StringParsedStream = require('./StringParsedStream');
var proto = require('proto');

var JSONParsedStream = proto.extend.call(StringParsedStream, {
	reviver: function(key, value){
		return value;
	},

	parse: function(string){
		return JSON.parse(string, this.reviver.bind(this));
	}
});

module.exports = JSONParsedStream;