require('@dmail/object/assign');

var methods = {
	sum: function(){
		var i = arguments.length, sum = 0, value;
		while(i--){
			value = arguments[i];
			if( isFinite(value) ){
				sum+= parseFloat(value);
			}
		}
		return sum;
	},

	average: function(){
		var length = arguments.length;
		return length === 0 ? 0 : this.sum.apply(this, arguments) / length;
	}
};

module.exports = methods;
Object.assign(Math, methods);