exports['simple test'] = function(){
	if( 1+1 != 2 ){
		throw new Error('1+1 not equal to 2');
	}
};

exports['test using assertion'] = function(test){
	test.equal(1+1, 2); //3); // uncomment this to get an AssertionError
};

exports['testing a module'] = function(test, foo){
	test.equal(foo.name, 'foo');
};

exports['async testing'] = function(test, foo){
	// set a default timeout for the willResolve assertions of this test
	test.Assertion.assertions.willResolve.timeout = 200; // the default is 100ms

	// set a specific timeout for this assertion
	var assertion = test.willResolve(Promise.resolve('foo'));
	assertion.timeout = 400; // must be resolve in 400ms max
};