var TestSerie = require('../TestSerie');
var proto = require('proto');

var FileTest = proto.extend(TestSerie, {
	type: 'file',
	path: null,

	constructor: function(path, tests){
		TestSerie.call(this, tests);
		this.name = 'Test in file : ' + path;
		this.path = path;
	}
});

module.exports = FileTest;