/*
nodetest peut être utilisé de plusieurs façon

soit on le lance comme un nodeprocess, soit comme un module par ex:

var nodetest = require('nodetest');

nodetest.testModule('es6'); // log in the console
nodetest.testModule('es6', logger); // log using logger
nodetest.testModule('es6', function(error, duration){
	// do something with error / duration
});
nodetest.testModuleRecursive('es6');

nodetest.testModuleAt('./folder');
nodetest.testModuleAtRecursive('./folder');
*/

var nodetest = {
	testModule: function(path, handler, bind){
		function listener(e, test){
			if( typeof handler == 'function' ){
				handler.apply(bind, arguments);
			}
			else if( typeof handler === 'object' ){
				if( e ) handler.error(e);
				else handler.log('tests passed in {duration}', {duration: test.duration});
			}
		}
	},

	testModuleRecursive: function(path, handler){

	},

	testModuleAt: function(path, handler){

	},

	testModuleAtRecursive: function(path, handler){

	}
};

module.exports = nodetest;