var unit = {
	Test: require('./test'),
	TestFile: require('./TestFile'),
	ModuleTest: require('./ModuleTest'),
	FolderTest: require('./FolderTest'),

	test: function(fn, modulePath){
		var moduleTest = this.ModuleTest.create(modulePath);

		moduleTest.testFilePaths = []; // dont check test files
		moduleTest.tests = [this.Test.create(fn)]; // just test this one

		return moduleTest;
	},

	testFile: function(filePath, modulePath){
		modulePath = modulePath || this.ModuleTest.FileCollector.getModuleFromTestFile(filePath);
		var moduleTest = this.ModuleTest.create(modulePath);

		moduleTest.testFilePaths = [filePath];

		return moduleTest;
	},

	testModule: function(modulePath){
		return this.ModuleTest.create(modulePath);
	},

	testFolder: function(folderPath){
		return this.FolderTest.create(folderPath);
	}
};

module.exports = unit;