var exports = {
	implementProperty: function(object, name, value){
		object.prototype[name] = value;
	}
};

if( Object.defineProperty ){
	exports.descriptor = {
		enumerable: false,
		writable: true,
		value: null
	};

	exports.implementProperty = function(object, name, value){
		this.descriptor.value = value;
		Object.defineProperty(object.prototype, name, this.descriptor);
	};
}

Object.implementProperty = exports.implementProperty;
module.exports = exports;