var property = require('property');
var clone = require('../clone');

var exports = {
	getValue: function(name, object){
		return object[name];
	},

	// set name in source cloning value and merging objects
	mergeProperty: function(name, source){
		var sourceValue = exports.getValue(name, source), targetValue;

		if( typeof sourceValue == 'object' && sourceValue !== null ){
			targetValue = exports.getValue(name, this);

			if( typeof targetValue == 'object' && targetValue !== null ){
				exports.mergeProperties(targetValue, sourceValue);
			}
			else{
				clone.assignPropertyClone.call(this, name, source);
			}
		}
		else{
			property.assignProperty.call(this, name, source);
		}

		return this;
	},

	mergeProperties: function(target, source, names){
		var i = 0, j, name;

		if( names ){
			j = names.length;
			for(;i<j;i++){
				name = names[i];
				if( Object.prototype.hasOwnProperty.call(source, name) ){
					this.mergeProperty.call(target, name, source);
				}
			}
		}
		else{
			property.forEachKey(source, this.mergeProperty, target);
		}

		return target;
	}
};

if( 'getOwnPropertyDescriptor' in Object ){

	// custom setter/getter are merged without being called
	exports.getValue = function(name, object){
		var descriptor = Object.getOwnPropertyDescriptor(object, name);
		return descriptor && 'value' in descriptor ? descriptor.value : null;
	};

}

exports.mergeProperties = exports.mergeProperties.bind(exports);
Object.merge = exports.mergeProperties;

module.exports = exports;
