// http://stackoverflow.com/questions/2332811/capitalize-words-in-string/7592235#7592235

function upperCase(a){
	return a.toUpperCase();
}

var regex = /\b[a-z]/g; // /(?:^|\s)\S/g

function capitalize(){
	return String(this).replace(regex, upperCase);
}

module.exports = capitalize;
require('object/polyfill')(String.prototype, 'capitalize', capitalize);