function truncate(length, motif){
	length = length || 0;
	motif = typeof motif == 'string' ? motif : 'â€¦';
	var str = String(this);
	return str.length > length ? str.slice(0, length - motif.length) + motif : str;
}

module.exports = truncate;
require('@dmail/object/polyfill')(String.prototype, 'truncate', truncate);