var Task = require('./task');

// returns a task not doing anything until a certain amount of ms
Task.prototype.delay = function(ms){
	var task = this.then();

	var timer = setTimeout(function(){
		task.become = Task.prototype.become;
		task.become(this);
	}.bind(this), ms);

	task.become = function(){

	};

	// clean timer
	task.clean = function(){
		clearTimeout(timer);
		return this.constructor.prototype.clean.call(this);
	};

	return task;
};

require('task/complete');

function delay(ms, value){
	return Task.complete(value).delay(ms);
}

module.exports = delay;
Task.delay = delay;