var Task = require('task');
require('task/any');
require('task/call');
require('iterator/map');

function mapAny(iterable, fn, bind){

	iterable = Iterator.map(iterable, function(value, index, iterable){
		return Task.call(fn, bind, value);
	}, bind);

	return Task.any(iterable);
}

module.exports = mapAny;
Task.mapAny = mapAny;