var StringTemplate = require('./StringTemplate');
var TemplateRepeater = require('./TemplateRepeater');
require('@dmail/object/assign');

Object.assign(StringTemplate, {
	TemplateRepeater: TemplateRepeater,

	repeat: function(array, separator, fn, bind){
		var repeater = this.TemplateRepeater.create(this, separator);
		repeater.exec(array, fn, bind);
		return repeater;
	}
});

String.prototype.render = function(scope){
	return StringTemplate.create(this).render(scope);
};

module.exports = StringTemplate;