exports['render on same template gives != result for != object'] = function(test, StringTemplate){
	var tpl = StringTemplate.create('Hello {name} !');

	test.equal(tpl.render({name: 'damien'}), 'Hello damien !');
	test.equal(tpl.render({name: 'sandra'}), 'Hello sandra !');
};

exports['toArray().join equivalent to toString()'] = function(test, StringTemplate){
	var tpl = StringTemplate.create('test');

	test.equal(tpl.toArray().join(''), tpl.toString());
};