var TestPrototype = require('../TestPrototype');

var TestSerie = TestPrototype.create({
	Test: require('../Test'),
	type: 'testSerie',
	name: 'Anonymous testSerie',
	path: null,
	module: null,
	tests: null,

	init: function(path, module){
		this.path = path;
		this.module = module;
		this.setExportedTests(this.load(this.path));
	},

	load: function(path){
		return require(path);
	},

	unload: function(path){
		delete require.cache[path];
	},

	reload: function(){
		this.unload(this.path);
		this.setExportedTests(this.load(this.path));
	},

	onChange: function(){
		this.reload();
		// si en cours de traitement ou terminÃ©, restart
		if( this.state === 'started' || this.state === 'ended' ) this.restart(); 
	},

	createTest: function(name, fn){
		return this.Test.new(name, fn, this.module);
	},

	add: function(name, fn){
		var test = this.createTest(name, fn);
		this.tests.push(test);
		return this;
	},

	setExportedTests: function(exports){
		this.tests = [];
		
		for(var name in exports){
			this.add(name, exports[name]);
		}
	},

	setModule: function(module){
		this.tests.forEach(function(test){
			test.module = module;
		});
	},

	setup: function(){
		this.index = 0;
		this.current = null;
	},

	teardown: function(){
		// something to do?
	},

	clean: function(){
		if( this.current ){
			this.current.stop();
		}
	},

	next: function(){
		if( this.index >= this.tests.length ){
			return null;
		}
		else{
			this.current = this.tests[this.index];
			this.index++;
			return this.current;
		}
	},

	nextTest: function(e){
		if( e ){
			/*
			var stackTrace = require('stackTrace');
			var call = stackTrace.findCallByFile(stackTrace.get(), this.testSerie.path);
			var line = call.getLineNumber();
			
						
			error.file = this.testSerie.path;
			error.line = line - 1;
			*/
			this.fail(e);
		}
		else if( this.next() ){
			this.current.start();
		}
		// all test passed with success or no test to run
		else{
			this.pass();
		}
	},

	_run: function(){
		this.nextTest();
	}
});

module.exports = TestSerie;